#!/usr/bin/env python3# -*- coding: utf-8 -*-"""Created on Fri Feb  3 13:44:14 2023@author: poojap"""def inputCoordinates():    (coordinate1) = input("Input coordinates for first point on plane in  format x1, y1: ")    coordinate1 = tuple(int(a) for a in coordinate1.split(","))    (coordinate2) = input("Input coordinates for second point on plane (x2, y2): ")    coordinate2 = tuple(int(a) for a in coordinate2.split(","))    print("You have entered coordinate 1 as: {}".format(coordinate1), " \nand coordinate 2 as {}".format( coordinate2))    return coordinate1, coordinate2if __name__ == "__main__":    inputCoordinates()